part.code:
static object_count;
local  time_to_live;

protected func Initialize()
{
  object_count++;
  time_to_live = 100;
}

protected func Destruction()
{
  object_count--;
}

protected func Timer()
{
  if(!--time_to_live)
  {
    RemoveObject();
    return();
  }

  var obj = FindObject(Clonk, 0, 0, -1, -1);

  if(ObjectDistance(obj) < 20)
    DoDamage(-10, obj);

  return();
}
