example.code:
func Activate(pCaller)
{
  pCaller->CreateMenu(GetID(), pCaller, 0, "Kein geeignetes Objekt in Reichweite!");
  var obj;
  while(obj = FindObject(0, -50, -50, 100, 100, OCF_Chop(), 0, 0, NoContainer(), obj))
    pCaller->AddMenuItem("%s fällen", Format("SetCommand(this(), \"Chop\", Object(%d))", obj->ObjectNumber()), obj->GetID());
}
