protected func ControlSpecial2()
  {
  var i, base;
  while (base=FindBase(GetOwner(), i++))
    if (PathFree(GetX(), GetY(), GetX(base), GetY(base)))
      {
      Message("Ich sehe meine Basis!", this());
      break;
      }
  if (!base) Message("Ich sehe meine Basis nicht :/", this());
  }
