example.code:
private func GetOwnMass(pClonk)
  {
  var iMass=pClonk->GetMass(), i, pObj;
  while(pObj = pClonk->Contents(i++)) iMass -= pObj->GetMass();
  return(iMass);
  }

protected func Activate(pByClonk)
  {
  [Essen]
  pByClonk->SetMass(GetOwnMass(pByClonk) + 100);
  pByClonk->Sound("ClonkMunch");
  return(RemoveObject());
  }
